var demo={};demo.ajax=function(a,b){$.ajax_extend({url:a,cache:false,type:"get",dataType:"json",data:"aaa=111",error:function(e,c,d){console.log("请求失败，发生错误",d);b.append("请求失败，发生错误！<br/>"+JSON.stringify(d)+"<br/>")},success:function(c){c=c||{};if(c.code!="1000"){b.append("请求失败！状态码错误<br/>");return}b.append("请求成功！<br/>"+JSON.stringify(c)+"<br/>")}},{errRetryLimit:3,errRetryInterval:1000,customRetryErr:function(c){return c.code!="1000"},errRetry:function(e,c,d,f){b.append("进行第"+f.toString()+"次重试<br/>")},requestDataFormat:function(c){return c},responseDataFormat:function(c){c.code="1000";return c}})};$(document).ready(function(){$("#btnReq").click(function(){var b=$("#reqUrl").val();var a=$("#resResult");a.empty();demo.ajax(b,a)})});